// App.js

// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

class MyButton extends React.Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    alert(`Você clicou no item ${this.props.item}!`);
  }

  render() {
    return (
      <button onClick={this.handleClick}>
        Item {this.props.item}
      </button>
    );
  }
}

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [date, setDate] = useState('');
  
  const handleSearch = async () => {
    try {
        const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
        const response = await axios.get(
          `https://newsapi.org/v2/everything?apiKey=${apiKey}&q=${keyword}&pageSize=20`
        );

        setNews(response.data.articles);

        // Envia as notícias para o backend
        await axios.post('http://localhost:3000/api/news', {
          news: response.data.articles
        });

        // Filtra as notícias com base na palavra-chave
        const filteredNews = response.data.articles.filter(
            (item) => item.title.toLowerCase().includes(keyword.toLowerCase())
        );

        // Ordena as notícias por data de publicação (mais recente primeiro)
        filteredNews.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));

        // Atualiza o estado das notícias no frontend
        setNews(filteredNews);
    } catch (error) {
        console.error('Erro ao buscar notícias:', error);
    }
};
   
const handleClick = async () => {
  try {
    // Faça sua busca aqui...
    // Supondo que você tenha os dados da notícia disponíveis em title, content e date

    for (const item of news) {
      // Insira os dados da notícia no banco de dados
      await axios.post('http://localhost:3000/api/news', {
        title: item.title,
        content: item.content,
        date: item.publishedAt
      });
    }

    console.log('Notícias adicionadas com sucesso!');
  } catch (error) {
    console.error('Erro ao adicionar notícias:', error.message);
  }

  // Define a palavra-chave, marca que o botão de busca foi clicado e chama a busca
  setKeyword(''); // Limpa o estado da palavra-chave se desejar
  setSearchClicked(true);
  handleSearch();
};

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button key={item} onClick={() => handleClick(item)}>
          Item {item}
        </button>
      </div>
      <ul>
        {news.map((item, index) => (
          <li key={index}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>Data de publicação: {new Date(item.publishedAt).toLocaleDateString()}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
