// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  const handleSearch = async () => {
    try {
      const response = await axios.post('/search', { keyword });
      const filteredNews = response.data.news.filter(item => item.keywordField === keyword);
      setNews(filteredNews);
    } catch (error) {
      console.error('Erro ao buscar notícias:', error);
    }
  };
  
  useEffect(() => {
    // Função para buscar notícias ao carregar o componente
    async function fetchNews() {
      try {
        if (searchClicked) {
          const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
          const response = await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}`);
          const data = await response.json();
          setNews(data.articles);
        }
      } catch (error) {
        console.error('Erro ao buscar notícias:', error);
      }
    }

    fetchNews();
  }, [searchClicked]); // Adicionando searchClicked à lista de dependências

  const handleClick = () => {
    // Define a palavra-chave, marca que o botão de busca foi clicado e chama a busca
    setKeyword(''); // Limpa o estado da palavra-chave se desejar
    setSearchClicked(true);
    handleSearch();
  };

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          id="keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button onClick={handleClick}>Buscar</button>
      </div>
      <ul>
        {news.map((item) => (
          <li key={item._id}>
            <h3>{item.title}</h3>
            <p>{item.content}</p>
            <p>Palavra-chave: {item.keyword}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
