// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [date, setDate] = useState('');
  
  const handleSearch = async () => {
    try {
        const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
        const toDate = new Date().toISOString().split('T')[0]; // Data atual
        const fromDate = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]; // Data de 7 dias atrás

        const response = await axios.get(
            `https://newsapi.org/v2/top-headlines?apiKey=${apiKey}&q=${keyword}&from=${fromDate}&to=${toDate}`
        );

        // Filtra as notícias com base na palavra-chave
        const filteredNews = response.data.articles.filter(
            (item) => item.title.toLowerCase().includes(keyword.toLowerCase())
        );

        // Ordena as notícias por data de publicação (mais recente primeiro)
        filteredNews.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));

        // Atualiza o estado das notícias no frontend
        setNews(filteredNews);
    } catch (error) {
        console.error('Erro ao buscar notícias:', error);
    }
};

   
  
  const handleClick = async () => {
    try {
      // Faça sua busca aqui...
      // Supondo que você tenha os dados da notícia disponíveis em title, content e date

      const response = await axios.post('http://localhost:3000/api/news', {
          title: "Título da notícia",
          content: "Conteúdo da notícia",
          date: "YYYY-MM-DD"
      });

      if (response.status === 200 || response.status === 201) {
          console.log('Notícia adicionada com sucesso!');
      } else {
          console.error('Erro ao adicionar notícia:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao adicionar notícia:', error.message);
    }
    // Define a palavra-chave, marca que o botão de busca foi clicado e chama a busca
    setKeyword(''); // Limpa o estado da palavra-chave se desejar
    setSearchClicked(true);
    handleSearch();
  };

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          id="keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button onClick={handleClick}>Buscar</button>
      </div>
      <ul>
        {news.map((item) => (
          <li key={item.title}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
