// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [date, setDate] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  
  const handleSearch = async () => {
    try {
        const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
        const response = await axios.get(
          `https://newsapi.org/v2/everything?apiKey=${apiKey}&q=${keyword}&pageSize=20`
        );

        setSearchResults([]);
      
        // Envia as notícias para o backend
        await axios.post('http://localhost:3000/api/news', {
          news: response.data.articles
        });

        // Filtra as notícias com base na palavra-chave
        const filteredNews = response.data.articles.filter(
            (item) => item.title.toLowerCase().includes(keyword.toLowerCase())
        );

        // Ordena as notícias por data de publicação (mais recente primeiro)
        filteredNews.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));

        // Atualiza o estado das notícias no frontend
        setNews(filteredNews);
    } catch (error) {
        console.error('Erro ao buscar notícias:', error);
    }
};

  // Iterar sobre cada notícia na lista de notícias
  for (let noticia of noticias) {
    // Criar um novo documento de notícia usando o modelo de dados
    const novaNoticia = new News({
        title: noticia.title,
        content: noticia.content,
        date: noticia.date
    });

  // Salvar a notícia no MongoDB
  novaNoticia.save()
      .then(savedNews => {
          console.log('Notícia adicionada com sucesso:', savedNews);
      })
      .catch(error => {
          console.error('Erro ao adicionar notícia:', error);
      });
}

console.log("Todas as notícias foram adicionadas ao MongoDB com sucesso!");
   
  const handleClick = async () => {
    try {
      // Faça sua busca aqui...
      // Supondo que você tenha os dados da notícia disponíveis em title, content e date
      
      const response = await axios.post('http://localhost:3000/api/news', {
          title: "string",
          content: "string",
          date: Date
      });

      if (response.status === 200 || response.status === 201) {
          console.log('Notícia adicionada com sucesso!');
      } else {
          console.error('Erro ao adicionar notícia:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao adicionar notícia:', error.message);
    }
    // Define a palavra-chave, marca que o botão de busca foi clicado e chama a busca
    setKeyword(''); // Limpa o estado da palavra-chave se desejar
    setSearchClicked(true);
    handleSearch();
  };

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          id="keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button onClick={handleClick}>Buscar</button>
      </div>
      <ul>
        {news.map((item) => (
          <li key={item.title}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>Data de publicação: {new Date(item.publishedAt).toLocaleDateString()}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
      <ul>
        {searchResults.map((result, index) => (
          <li key={index}>
            <h3>{result.title}</h3>
            <p>{result.content}</p>
            <p>{result.date}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
