// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  const handleSearch = async () => {
    try {
      const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
      const response = await axios.get(
        `https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}&q=${keyword}&from=2022-01-01&to=2022-12-31`
      );
  
      // Filtra as notícias com base na palavra-chave
      const filteredNews = response.data.articles.filter(
        (item) => item.title.toLowerCase().includes(keyword.toLowerCase())
      );
  
      setNews(filteredNews);
    } catch (error) {
      console.error('Erro ao buscar notícias:', error);
    }
  };
   
  const handleClick = () => {
    // Define a palavra-chave, marca que o botão de busca foi clicado e chama a busca
    setKeyword(''); // Limpa o estado da palavra-chave se desejar
    setSearchClicked(true);
    handleSearch();
  };

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          id="keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button onClick={handleClick}>Buscar</button>
      </div>
      <ul>
        {news.map((item) => (
          <li key={item.title}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
