// App.js

// Importe os módulos necessários
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [date, setDate] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    try {
      const apiKey = 'e2b489b9e52d4baf8d0b090a9c1d9319';
      const response = await axios.get(
        `https://newsapi.org/v2/everything?apiKey=${apiKey}&q=${keyword}&pageSize=20`
      );

      // Salvar os resultados da busca no estado
      setNews(response.data.articles);

      // Enviar os resultados da busca para o backend para salvar no banco de dados
      await axios.post('http://localhost:3000/api/news/add', {
        newsList: response.data.articles
      });

      // Marcar que a busca foi realizada
      setSearchClicked(true);
    } catch (error) {
      console.error('Erro ao buscar notícias:', error);
    }
  };

  return (
    <div>
      <h1>Notícias Globais</h1>
      <div>
        <label>Pesquisar por palavra-chave: </label>
        <input
          type="text"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button onClick={handleSearch}>Buscar</button>
      </div>
      <ul>
        {news.map((item, index) => (
          <li key={index}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>Data de publicação: {new Date(item.publishedAt).toLocaleDateString()}</p>
            {/* Outros detalhes da notícia... */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
